ruleToc.chapterList:
@js:
let id=java.get('id');
let data = JSON.parse(result);
let chapterList = [];

data['list'].forEach(
    list => {
        let url = `https://story.udn.com/dcstore/ShowStoreProductChapterContent?id=${id}&chapter_id=${list.chapter_id}`;
        let isfree = (list.isfree=="Y" ? "":"💰");
        chapterList.push(
        {
            title: String(isfree+list.title),
            url: String(url)
        });
    });

chapterList
