ruleToc.chapterList:
@js:
let data = JSON.parse(result);
let novelvolumes = data['data']['novel']['volumes'];
let volumes = data['data']['volumes'];
let chapterList = [];
let index = 0;

novelvolumes.forEach(
    list => {
        if (volumes[index] != null) {
            let chapurl = `https://api.czbooks.net/chapter/${index}`;
            chapterList.push(
            {
                title: String('════  '+list.name+'  ════'),
                url: String(chapurl)
            });

            volumes[index].forEach(
                item => {
                    let url = `https://api.czbooks.net/chapter/detail?novelId=${item.novelId}&chapterId=${item.chapterId}`;
                    chapterList.push(
                    {
                        title: String(item.chapterName),
                        url: String(url)
                    });
                });
        }

        index++;
    });

chapterList
